package LearnStatic;

public class LoopAndOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("j values");
		System.out.println("----------------");
		for(int j=0;j<=3;++j)
		{
			System.out.println(j); //0123
		}
		System.out.println("i values");
		System.out.println("----------------");
		for(int i=0;i<=3;i++)
		{
			System.out.println(i);  //0123
		}
	
	}

}
