package Strings;

public class StringSubstring {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String s="new string for ";
		System.out.println(s.substring(1));//1- starts index
		
		System.out.println(s.substring(0, 3)); // 0,3 - Starting from index 0 and ends at 3
	}

}
