package Strings;

public class StringTrim {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s=" test trim  ";
		System.out.println(s);
		System.out.println(s.length());
		String s1=s.trim();
		System.out.println(s1);
		System.out.println(s1.length());
		
		
	}
		
}
